CREATE TABLE "css_petition_policy"
(
      "id" bigint PRIMARY KEY NOT NULL
    , "petition_id" bigint
    , "policy_type" varchar(1)
    , "policy_no" varchar(15)
    , "plan_code" varchar(10)
    , "plan_name" varchar(255)
    , "create_date" timestamptz(35)
);
COMMENT ON COLUMN "css_petition_policy"."id" IS 'รหัสตารางกรมธรรม์';
COMMENT ON COLUMN "css_petition_policy"."petition_id" IS 'รหัสอ้างอิงตารางใบคำร้อง';
COMMENT ON COLUMN "css_petition_policy"."policy_type" IS 'ประเภทกรมธรรม์';
COMMENT ON COLUMN "css_petition_policy"."policy_no" IS 'เลขที่กรมธรรม์';
COMMENT ON COLUMN "css_petition_policy"."plan_code" IS 'แบบประกัน';
COMMENT ON COLUMN "css_petition_policy"."plan_name" IS 'ชื่อแบบประกัน';
COMMENT ON COLUMN "css_petition_policy"."create_date" IS 'วันทีทำรายการ';

CREATE TABLE "css_petition_request"
(
      "id" bigint PRIMARY KEY NOT NULL
    , "request_no" varchar(15)
    , "request_type" varchar(10)
    , "customer_id" varchar(10)
    , "customer_card_no" varchar(50)
    , "customer_title" varchar(50)
    , "customer_name" varchar(100)
    , "customer_sname" varchar(100)
    , "create_date" timestamptz(35)
);
COMMENT ON COLUMN "css_petition_request"."id" IS 'รหัสตารางใบคำร้อง';
COMMENT ON COLUMN "css_petition_request"."request_no" IS 'เลขที่ใบคำขอ';
COMMENT ON COLUMN "css_petition_request"."request_type" IS 'ประเภทใบคำร้อง';
COMMENT ON COLUMN "css_petition_request"."customer_id" IS 'รหัสลูกค้า';
COMMENT ON COLUMN "css_petition_request"."customer_card_no" IS 'เลขประจำตัวลูกค้า';
COMMENT ON COLUMN "css_petition_request"."customer_title" IS 'คำนำหน้าชื่อ';
COMMENT ON COLUMN "css_petition_request"."customer_name" IS 'ชื่อลูกค้า';
COMMENT ON COLUMN "css_petition_request"."customer_sname" IS 'นามสกุลลูกค้า';
COMMENT ON COLUMN "css_petition_request"."create_date" IS 'วันที่ทำรายการ';

