CREATE TABLE "css_payment"
(
      "id" bigint PRIMARY KEY NOT NULL
    , "order_no" varchar(15) NOT NULL
    , "invoice_no" varchar(15) NOT NULL
    , "customer_id" varchar(10) NOT NULL
    , "customer_id_no" varchar(50) NOT NULL
    , "customer_title" varchar(50) NOT NULL
    , "customer_name" varchar(100) NOT NULL
    , "customer_sname" varchar(100) NOT NULL
    , "customer_tel" varchar(50) NOT NULL
    , "customer_email" varchar(255) NULL
    , "payment_amount" float(19) NOT NULL
    , "payment_fee" float(19) NULL
    , "payment_discount" float(19) NULL
    , "net_payment" float(19) NOT NULL
    , "send_2c2p_status" varchar(1) NOT NULL
    , "send_hydra_status" varchar(1) NULL
    , "policy_no" varchar(15) NOT NULL
    , "policy_type" varchar(1) NOT NULL
    , "policy_year" int NOT NULL
    , "plan_code" varchar(10) NOT NULL
    , "plan_name" varchar(255) NOT NULL
    , "plan_description" varchar(255) NULL
    , "policy_branch_code" varchar(10) NULL
    , "policy_branch_name" varchar(255) NULL
    , "policy_branch_tel" varchar(50) NULL
    , "policy_branch_code7" varchar(10) NULL
    , "premium" float(19) NULL
    , "premium_per_month" float(19) NULL
    , "agent_code7" varchar(7) NULL
    , "agent_code5" varchar(5) NULL
    , "version" varchar(5) NULL
