CREATE TABLE "css_loan"
(
	"loan_id"	bigint	PRIMARY KEY NOT NULL
	,"request_no"	varchar(20)	NOT NULL
	,"policy_no"	varchar(15)	NOT NULL
	,"policy_type"	varchar(1)	NOT NULL
	,"plan_code"	varchar(15)	NOT NULL
	,"plan_name"	varchar(255)	NOT NULL
	,"request_date"	timestamptz(35)	NOT NULL
	,"loan_amount"	numeric(10, 2)	NOT NULL
	,"net_balance"	numeric(10, 2)	NOT NULL
	,"customer_card_no"	varchar(50)	NOT NULL
	,"customer_title"	varchar(50)	NOT NULL
	,"customer_name"	varchar(100)	NOT NULL
	,"customer_sname"	varchar(100)	NOT NULL
	,"create_date"	timestamptz(35)	NOT NULL
);
COMMENT ON COLUMN "css_loan"."loan_id" IS 'Running number';
COMMENT ON COLUMN "css_loan"."request_no" IS 'เลขที่ใบคำขอ';
COMMENT ON COLUMN "css_loan"."policy_no" IS 'เลขที่กรมธรรม์';
COMMENT ON COLUMN "css_loan"."policy_type" IS 'ประเภทกรมธรรม์';
COMMENT ON COLUMN "css_loan"."plan_code" IS 'แบบประกัน';
COMMENT ON COLUMN "css_loan"."plan_name" IS 'ชื่อแบบประกัน';
COMMENT ON COLUMN "css_loan"."request_date" IS 'วันที่ทำรายการ(วันที่ขอกู้)';
COMMENT ON COLUMN "css_loan"."loan_amount" IS 'จำนวนเงินที่ขอกู้';
COMMENT ON COLUMN "css_loan"."net_balance" IS 'คงเหลือสุทธิ';
COMMENT ON COLUMN "css_loan"."customer_card_no" IS 'เลขประจำตัวลูกค้า';
COMMENT ON COLUMN "css_loan"."customer_title" IS 'คำนำหน้าชื่อ';
COMMENT ON COLUMN "css_loan"."customer_name" IS 'ชื่อลูกค้า';
